) VALUES (
    ?SERVICE_PERIOD, ?FIN_DIV_NAME, ?FIN_SUBDIV_NAME, ?BILL_AREA_ID, ?BILL_AREA_NAME, ?POS_ID, ?POS_NAME, ?LOC_ID, ?LOC_NAME, ?GL_PREFIX, ?GL_INDEX, ?PL_CATEGORY, ?ORIG_PLAN_ID, ?ORIG_FSC, ?ADJUSTMENT_CATEGORY, ?CHARGE, ?PAYMENT, ?ADJUSTMENT, ?ORIG_CHARGE_POST_PERIOD
);
